/* Function  structure for function  `GET_CHILD_ENT` */

/*!50003 DROP FUNCTION IF EXISTS `GET_CHILD_ENT` */;
DELIMITER $$

/*!50003 CREATE DEFINER=`root`@`%` FUNCTION `GET_CHILD_ENT`(ENTCODE VARCHAR(32)) RETURNS varchar(4000) CHARSET utf8
BEGIN
DECLARE STEMP VARCHAR(4000);
DECLARE STEMPCHD VARCHAR(4000);
SET STEMP='$';
SET STEMPCHD = CAST(ENTCODE AS CHAR);
WHILE STEMPCHD IS NOT NULL DO
SET STEMP= CONCAT(STEMP,',',STEMPCHD);
SELECT GROUP_CONCAT(ENT_CODE) INTO STEMPCHD FROM plat_ent WHERE FIND_IN_SET(PARENT_CODE,STEMPCHD)>0;
END WHILE;
RETURN STEMP;
END */$$
DELIMITER ;

